func:
// Get values from flow context
var diningSP = flow.get("dining_SP") || 0; // Default to 0 if not set
var diningHyst = flow.get("dining_hyst") || 0; // Default to 0 if not set

// Calculate dining_targetHIGH and dining_targetLOW
var diningTargetHIGH = diningSP + diningHyst;
var diningTargetLOW = diningSP - diningHyst;

// Set flow variables
flow.set("dining_targetHIGH", diningTargetHIGH);
flow.set("dining_targetLOW", diningTargetLOW);

return msg;
